  const [] = useState([]);
  const fetchSilent = () => {
    db.collection("players")
      .where("category", "==", "silent")
      .get()
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          console.log(doc.id, "=>", doc.data());
          setSilentPlayers({ id: doc.id, data: doc.data() });
          console.log(silentPlayers);
