import React, { useState } from "react";
// import { Bids } from "../../store/actions/playerActions";
import { connect } from "react-redux";
import firebase from "firebase";

import { withStyles, makeStyles } from "@material-ui/core/styles";
import {
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@material-ui/core";
import { db } from "../../config/Firebase";
// import classes from "*.module.css";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const SilentBiddingHelper = ({ player, playerId, auth }) => {
  const [nextBid, setnextBid] = useState(parseInt(player.baseprice));
  console.log(player);
  console.log(playerId);
  const Bids = ({ playerId, biddingprice }) => {
    const teamId = auth.uid;
    console.log("Actions----");
    db.collection("players")
      .doc(playerId)
      .collection("Bids")
      .doc(teamId)
      .set(
        {
          bid: [
            {
              biddingprice: 250,
              timestamp: firebase.firestore.Timestamp.now(),
            },
          ],
        },
        { merge: true }
      )
      .then(() => {
        console.log("Added");
      });

    const maxi = 0;
    db.collection("players")
      .where("name", "==", String(playerId))
      .get()
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          console.log("Max:", doc.data().maxbid);
          maxi = doc.data().maxbid;
        });
      });
    if (maxi < biddingprice) {
      db.collection("players").doc(playerId).update({
        maxbid: biddingprice,
        maxbidBy: teamId,
      });
    }
  };

  return (
    <>
      {/* <h1>{playerId}</h1> */}

      <StyledTableCell>{player.name}</StyledTableCell>
      <StyledTableCell>{player.Runs}</StyledTableCell>
      <StyledTableCell>{player.Batavg}</StyledTableCell>
      <StyledTableCell>{player.strikerate}</StyledTableCell>
      <StyledTableCell></StyledTableCell>
      <StyledTableCell></StyledTableCell>
      <StyledTableCell></StyledTableCell>
      <StyledTableCell>{player.baseprice}</StyledTableCell>
      <StyledTableCell>{player.maxbid}</StyledTableCell>
      <StyledTableCell>
        <form onSubmit={Bids(playerId, nextBid)}>
          <input
            value={nextBid}
            onChange={(event) => {
              event.preventDefault();
              setnextBid(event.target.value);
            }}
          />
          <button
            type="submit"
            /* onClick={(event) => {
              event.preventDefault();
              Bids(playerId, nextBid);
            }} */
          >
            Bid
          </button>
        </form>
      </StyledTableCell>
    </>
  );
};
const mapStateToProps = (state) => {
  return {
    authError: state.auth.authError,
    auth: state.firebase.auth,
    playerB: state.playerB,
  };
};

/* const mapDispatchToProps = (dispatch) => {
  return {
    Bids: (playerId, biddingprice) => dispatch(Bids(playerId, biddingprice)),
  };
}; */
export default connect(mapStateToProps)(SilentBiddingHelper);
