import React, { useEffect, useState } from "react";
import { db } from "../../config/Firebase";
import Categories from "./Categories";
import Players from "./Players";
const PlayerSection = () => {
  const [categories, setCategories] = useState([]);
  // let categories = [];
  const [team, setTeam] = useState([]);

  const [Players, setPlayers] = useState([]);

  const fetchTeam = () => {
    db.collection("users").onSnapshot((snapshot) => {
      snapshot.docs.map((doc) => {
        if (categories === null) {
          setCategories({ id: doc.id, data: doc.data().teamName });
        } else {
          setCategories((categories) => [
            ...categories,
            {
              id: doc.id,
              data: doc.data().teamName,
            },
          ]);
        }
      });

      /* const result = snapshot.docs.map((doc) => ({
        id: doc.id,
        data: doc.data(),
      }));
      result.map((res) => {
        console.log(res.data);
        // categories.push([res.data.teamName]);
      }); */
    });
  };

  const filterPlayers = (category, id) => {
    console.log("C", id);
    db.collection("players")
      .where("team", "==", id)
      .onSnapshot((snapshot) => {
        snapshot.docs.forEach((doc) => {
          console.log(doc.data());
          setTeam((team) => [...team, { id: doc.id, data: doc.data() }]);
        });
      });
  };
  console.log(categories);

  useEffect(() => {
    fetchTeam();
  }, []);

  return (
    <div>
      <h1>Hello</h1>
      {categories === null ? (
        console.log("No category")
      ) : (
        <Categories filterPlayers={filterPlayers} categories={categories} />
      )}
      <Players players={team} />
    </div>
  );
};

export default PlayerSection;
