import React, { useState } from "react";
import { Bids } from "../store/actions/playerActions";
const SilentBiddingHelper = ({ player, playerId }) => {
  const [nextBid, setnextBid] = useState(0);
  console.log(player);
  return (
    <div>
      <table>
        <tr>
          <th>Name</th>
          <th>Runs</th>
          <th>Batting Avg</th>
          <th>StrikeRate</th>
          <th>Wickets</th>
          <th>Bowling Avg</th>
          <th>Economy</th>
          <th>Baseprice</th>
        </tr>
        <h2>{player.name}</h2>
        <h4>{player.Runs}</h4>
        <h4>{player.Batavg}</h4>
        <h4>{player.strikerate}</h4>
        <h4>{player.baseprice}</h4>
      </table>

      <form>
        <input
          value={nextBid}
          onChange={(event) => {
            event.preventDefault();
            setnextBid(event.target.value);
          }}
        />
        <button type="submit" onClick={Bids(playerId, nextBid)}>
          Bid
        </button>
      </form>
    </div>
  );
};

export default SilentBiddingHelper;
