import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import { teamStats } from "../store/actions/playerActions";
import { db } from "../config/Firebase";
import General from "./Dashboards/General";

import { Typography } from "@material-ui/core";
import { createMuiTheme } from "@material-ui/core/styles";
import PlayerSection from "./Dashboards/PlayerSection";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

const theme = createMuiTheme({
  palette: {
    text: {
      primary: "#FFFFFF",
    },
  },
});

const Dashboard = ({ auth }) => {
  const [team, setTeam] = useState(null);
  const classes = useStyles();
  const fetchTeam = () => {
    db.collection("users").onSnapshot((snapshot) => {
      const result = snapshot.docs.map((doc) => ({
        id: doc.id,
        data: doc.data(),
      }));
      result.map((res) => {
        if (res.id === auth.uid) {
          // console.log(res.data.teamBalance);
          // console.log(typeof res.data.teamBalance);
          setTeam(res.data);
        }
      });
    });
  };
  console.log(team);
  if (team === null) console.log("NULL");
  useEffect(() => {
    fetchTeam();
  }, []);

  useEffect(() => {
    db.collection("players")
      .where("category", "==", "silent")
      .where("status", "==", "close")
      .onSnapshot((snapshot) => {
        if (snapshot.exists) {
          snapshot.docs.map((doc) => {
            console.log(doc.data().name);
            console.log(doc.data().maxbidBy);
            db.collection("players").doc(doc.data().name).update({
              team: doc.data().maxbidBy,
            });
            const ref3 = db.collection("users").doc(doc.data().maxbidBy);

            ref3.onSnapshot((snapshot) => {
              if (snapshot.exists) {
                console.log(snapshot.data().teamBalance);
                ref3.update({
                  teamBalance:
                    parseInt(snapshot.data().teamBalance) -
                    parseInt(doc.data().maxbid),
                });
              }
            });
          });
        }
      });
  }, []);

  if (!auth.uid) return <Redirect to="/signin" />;
  return (
    <div className={classes.root}>
      <Grid container spacing={2}>
        <Grid item xs={3}>
          <Paper className={classes.paper}>
            {team === null ? console.log("No team") : <General player={team} />}
          </Paper>
        </Grid>
        <Grid item xs={9}>
          <Paper className={classes.paper}>
            <PlayerSection />
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    authError: state.auth.authError,
    auth: state.firebase.auth,
  };
};

/* const mapDispatchToProps = (dispatch) => {
  return {
    signIn: (creds) => dispatch(signIn(creds)),
  };
}; */

export default connect(mapStateToProps)(Dashboard);
