import React, { useEffect, useState } from "react";
import { db } from "../../config/Firebase";
import Categories from "./Categories";
import Players from "./Players";
const PlayerSection = () => {
  const [categories, setCategories] = useState(null);
  // let categories = [];
  const [team, setTeam] = useState([]);

  const fetchTeam = () => {
    db.collection("users").onSnapshot((snapshot) => {
      snapshot.docs.map((doc) => {
        if (categories === null) {
          setCategories([doc.data().teamName]);
        } else {
          setCategories([...categories, doc.data().teamName]);
        }
      });

      /* const result = snapshot.docs.map((doc) => ({
        id: doc.id,
        data: doc.data(),
      }));
      result.map((res) => {
        console.log(res.data);
        // categories.push([res.data.teamName]);
      }); */
    });
  };

  const filterPlayers = (category) => {
    console.log("C", category);
    db.collection("players")
      .where("team", "==", category)
      .onSnapshot((snapshot) => {
        snapshot.forEach((doc) => {
          console.log;
          setTeam((team) => [...team, { id: doc.id, data: doc.data() }]);
        });
      });
  };
  console.log(categories);

  useEffect(() => {
    fetchTeam();
  }, []);

  return (
    <div>
      <h1>Hello</h1>
      {categories === null ? (
        console.log("No category")
      ) : (
        <Categories filterPlayers={filterPlayers} categories={categories} />
      )}
      <Players players={team} />
    </div>
  );
};

export default PlayerSection;
